        const invTx = invokeScript({fee:INV_FEE, 
            dApp: address(accounts.auction),
            call: {
                function:"startAuction",
                args:[
                    {type:"integer", value: auctionDuration}, 
                    {type:"integer", value: 1000000}, 
                    {type:"string", value: "WAVES"}
                ]}, 
                payment: [
                    {amount: 1, assetId:issueTxId }
