        const invTx = invokeScript({fee:INV_FEE, dApp: address(accounts.auction), call: {function:"bid", args:[{type:"string", value: auctionId}]}, 
                        payment: [{amount: 999999, assetId:null }]}, 
                        accounts.customer2)
