    sigVerify(tx.bodyBytes, tx.proofs[0], base58'FYCT9GxhR2igEeyf9SWGi85bebBVaTAf9WUihEQnnBa9')
    #true
}

// ATTENTION: Tests can work incorrectly if you use nodes with cashed requests for balances
// It could happen that 'nodes-testnet.wavesnodes.com' has that kind of cache

const WAVES = 10 ** 8;

const SETSCRIPT_FEE = 0.01 * WAVES
const ISSUE_FEE = 1 * WAVES
const INV_FEE = 0.005 * WAVES
const ADD_FEE = 0.004 * WAVES
const MIN_FEE = 0.001 * WAVES

const BET_TYPE_NUMBER = 0
const BET_TYPE_RED_BLACK = 1
const BET_TYPE_EVEN_ODD = 2
const BET_TYPE_DESK_HALF = 3
const BET_TYPE_DESK_THIRD = 4
const BET_TYPE_ROW = 5

const betAmount = 1.5 * WAVES

async function rememberBalances(text, forAddress) {
    const wavesBal = await balance(forAddress)

    console.log (text + ": " + wavesBal + " WAVES")

    return wavesBal
}

function makeBet(casinoPubKey, roundId, betAmount, betType, guess, playerPubKey) {

    const bet = invokeScript({fee:INV_FEE, dApp: address(casinoPubKey), 
        call: {function:"bet", args:[{type:"string", value: roundId}, {type:"integer", value: betType}, {type:"integer", value: guess}]}, 
        payment: [{amount: betAmount, assetId:null }]}, 
        playerPubKey)

    console.log("Bet. round: '" + roundId + "', amount: " + betAmount / 10 ** 8 + ", betType: " + betType + ", guess: '" + guess + "'")
    return bet
}

function withdraw(casinoPubKey, roundId, playerPubKey) {

    const withdrawTx = invokeScript({fee:INV_FEE, dApp: address(casinoPubKey), 
        call: {function:"withdraw", args:[{type:"string", value: roundId}]}, 
        payment: []}, 
        playerPubKey)

    return withdrawTx
}

async function oracleStopRound(oraclePublicKey, roundId) {

    const stopRound = data({data:[{key:roundId + '_stop', value:true, type: 'boolean'}]}, oraclePublicKey)

    await broadcast(stopRound)
    await waitForTx(stopRound.id)
    console.log("Stop round: " + roundId)
}

func makeOracleResultForNumber() {
    const s = "0" + theAnswer
    // TODO:
    // Currently emulating just bet on number. These "12345" digits must be calculated in other way
    // "1" must be changed to black/red indication
    // "2" must be changed to even/odd indication
    // etc.
    return s.substr(s.length - 2) + "12345"
}

async function oraclePublishCorrectAnswer(oraclePublicKey, roundId, theAnswer) {

    const answerStr = makeOracleResultForNumber(theAnswer)

    const oraclePublishAnswer = data({data:[{key:roundId, value:answerStr, type: 'string'}]}, oraclePublicKey)

    await broadcast(oraclePublishAnswer)
    await waitForTx(oraclePublishAnswer.id)
    console.log("Published result for round '" + roundId + "': " + answerStr)
}


describe('Casino script test suite', async function () {

    this.timeout(100000);

    before(async function () {

        await setupAccounts({casino: SETSCRIPT_FEE + 35 * betAmount, 
                             player1: 2*betAmount + 2*INV_FEE, 
                            player2: betAmount + 2*INV_FEE, 
                            player3: betAmount + 2*INV_FEE, 
                            oracle: 2 * INV_FEE});

        const scriptC = compile(file('casino.ride').replace('$ORACLE_ADDRESS', address(accounts.oracle)));
        const ssTx = setScript({script:scriptC}, accounts.casino);
        await broadcast(ssTx);
        await waitForTx(ssTx.id)
        console.log('Script has been set')
    });    

    const roundId = "Round 1"
    const correctAnswer = 12

    it('Making bets', async function(){

        const bet1 = makeBet(accounts.casino, roundId, betAmount, BET_TYPE_NUMBER, 11, accounts.player1)
        await broadcast(bet1)
        const bet2 = makeBet(accounts.casino, roundId, betAmount, BET_TYPE_NUMBER, 10, accounts.player2)
        await broadcast(bet2)
        const bet3 = makeBet(accounts.casino, roundId, betAmount, BET_TYPE_NUMBER, correctAnswer, accounts.player3)
        await broadcast(bet3)

        await waitForTx(bet1.id)
        await waitForTx(bet2.id)
        await waitForTx(bet3.id)
    })

    it('Inability to make bet after freeze round', async function(){

        await oracleStopRound(accounts.oracle, roundId)

        const bet1 = makeBet(accounts.casino, roundId, betAmount, BET_TYPE_NUMBER, 14, accounts.player1)
        await expect(broadcast(bet1)).rejectedWith("This round is already played")
        console.log("Bet must failed because round finished")        
    })

    it('Withdraw test', async function(){

        await oraclePublishCorrectAnswer(accounts.oracle, roundId, correctAnswer)

        const withdraw1 = withdraw(accounts.casino, roundId, accounts.player1) 
        const withdraw2 = withdraw(accounts.casino, roundId, accounts.player2) 
        const withdraw3 = withdraw(accounts.casino, roundId, accounts.player3) 

        const casinoBefore = await rememberBalances("casino before: ", address(accounts.casino))
        const winnerBefore = await rememberBalances("winner before: ", address(accounts.player3))

        expect(broadcast(withdraw1)).rejectedWith("You won nothing this round")
        expect(broadcast(withdraw2)).rejectedWith("You won nothing this round")
        await broadcast(withdraw3)
        await waitForTx(withdraw3.id)

        const casinoAfter = await rememberBalances("casino after: ", address(accounts.casino))
        const winnerAfter = await rememberBalances("winner after: ", address(accounts.player3))

        expect(casinoAfter).to.equal(casinoBefore - 36 * betAmount, "Casino account reduced by win amount")
        expect(winnerAfter).to.equal(winnerBefore + 36 * betAmount - INV_FEE, "Winner account got win amount")
    })


})

# This is an example of a pool contract for add liquidity scenario.
#
# Increases pool liquidity using attached WAVES tokens for leasing and USDN tokens for staking.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let usdN = base58''
let staker = Address(base58'')
let leasePool = Address(base58'')
let shareToken = base58''

@Callable(i)
func addLiquidity() = {
  if (i.payments[0].assetId != unit || i.payments[1].assetId != usdN)
    then
      throw("unexpected assets")
    else {
      strict r = invoke(staker, "stake", [], [i.payments[1]])
      let stakingAmount = match r {
        case a: Int => a
        case _      => throw("unexpected result type from the staker")
      } 

      let stakingAmountKey = "STAKING_AMOUNT_" + i.caller.toString()
      let leaseAmountKey   = "LEASE_AMOUNT_" + i.caller.toString()
      let idKey            = "ID_" + i.caller.toString()

      let leasedAmount = this.getInteger(leaseAmountKey).valueOrElse(0)
      let leaseId      = this.getString(idKey)

      let newLease   = Lease(leasePool, leasedAmount + i.payments[0].amount)
      let newLeaseId = calculateLeaseId(newLease)

      let cancel = match leaseId {
        case id: String => [ LeaseCancel(fromBase58String(id)) ]
        case _          => [ StringEntry(idKey, toBase58String(newLeaseId)) ]
      }

      cancel ++
      [
        newLease,
        ScriptTransfer(i.caller, 1, shareToken),
        IntegerEntry(leaseAmountKey, leasedAmount + i.payments[0].amount),
        IntegerEntry(stakingAmountKey, stakingAmount)
      ]
    }
}

# This is an example of a staker contract for add liquidity scenario.
#
# Receives USDN and saves the new staking amount in the state.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let usdN = base58''

@Callable(i)
func stake() = {
  if (i.payments[0].assetId != usdN)
    then
      throw("unexpected asset")
    else {
      let currentLiquidity = this.getInteger(i.originCaller.toString()).valueOrElse(0)
      let newLiquidity = currentLiquidity + i.payments[0].amount
      (
        [ IntegerEntry(i.originCaller.toString(), newLiquidity) ],
        newLiquidity
      )
    }
}

# This is an example of a borrower contract for flash loan scenario.
#
# Borrows token A, changes to B, then changes the funds received again to A,
# returns the money to the lender and stays in profit on token A.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let loanFeePercent = 5
let assetA = base58''
let assetB = base58''
let exchangerABAddress = Address(base58'')
let exchangerBAAddress = Address(base58'')
let loanerAddress      = Address(base58'')

@Callable(i)
func trade(beneficiary: ByteVector) = {
  let loanAmount =
    if (i.payments[0].assetId == assetA)
      then i.payments[0].amount
      else throw("unexpected payment asset")

  strict startBalanceB = this.assetBalance(assetB)
  strict r1 = invoke(exchangerABAddress, "exchangeAB", [], [AttachedPayment(assetA, loanAmount)])
  strict diffB = this.assetBalance(assetB) - startBalanceB

  strict r2 = invoke(exchangerBAAddress, "exchangeBA", [], [AttachedPayment(assetB, diffB)])

  let debt = loanAmount.fraction(100 + loanFeePercent, 100)
  let profit = this.assetBalance(assetA) - debt
  [
    ScriptTransfer(Address(beneficiary), profit, assetA),
    ScriptTransfer(loanerAddress, debt, assetA)
  ]
}

# This is an example of a first exchanger contract for flash loan scenario.
#
# Changes A to B and transfers B to the address of the calling contract.
# Returns transfer amount.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let aToBRate = 5
let assetA = base58''
let assetB = base58''

@Callable(i)
func exchangeAB() = {
  if (i.payments[0].assetId == assetA)
    then
      [ ScriptTransfer(i.caller, i.payments[0].amount * aToBRate, assetB) ]
     else
      throw("unexpected token")
}

# This is an example of a second exchanger contract for flash loan scenario.
#
# Changes B to A and transfers A to the address of the calling contract.
# Returns transfer amount.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let exchangeRate = 5
let exchangeRateDiffPercent = 20
let aToBRate = exchangeRate.fraction(100 - exchangeRateDiffPercent, 100)
let assetA = base58''
let assetB = base58''

@Callable(i)
func exchangeBA() = {
  if (i.payments[0].assetId == assetB)
    then
      [ ScriptTransfer(i.caller, i.payments[0].amount / aToBRate, assetA) ]
     else
      throw("unexpected token")
}

# This is an example of a loaner contract for flash loan scenario.
#
# Should be called from the account related to the borrower.
#
# Calls the contract specified by the borrower when called in the transaction
# and applies the requested amount of token A in the call.
# Checks that upon completion of the call to the contract, the balance of token A has increased.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let assetA = base58''
let loanFeePercent = 5

@Callable(i)
func loan(amount: Int, callback: String, borrower: ByteVector) = {
  strict startBalance = this.assetBalance(assetA)
  strict r = invoke(Address(borrower), callback, [i.caller.bytes], [AttachedPayment(assetA, amount)])

  let balanceDiff = this.assetBalance(assetA) - startBalance
  let profit      = amount.fraction(loanFeePercent, 100)

  if (balanceDiff < profit)
    then
      throw("debt is not paid: diff=" + balanceDiff.toString() + ", expected=" + profit.toString())
    else
      []
}

# This is an example of a exchanger contract for swap scenario.
#
# Calls the USDN staking contract canceling USDN staking for the amount
# that the user should receive during the exchange
# and transfers USDN to the user's address.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let usdN = base58''
let exchangeRate = 5
let staker = Address(base58'')

@Callable(i)
func exchangeWavesUsdN() = {
  if (i.payments[0].assetId != unit)
    then
      throw("unexpected asset")
    else {
      strict r = invoke(staker, "cancelStake", [i.payments[0].amount * exchangeRate], [])
      [ ScriptTransfer(i.caller, i.payments[0].amount * exchangeRate, usdN) ]
    }
}

# This is an example of a staker contract for swap scenario.
#
# "stake" receives USDN and saves the new staking amount in the state.
# "cancelStake" withdraws the required amount from staking and transfers it to the account.

{-# STDLIB_VERSION 5     #-}
{-# SCRIPT_TYPE ACCOUNT  #-}
{-# CONTENT_TYPE DAPP    #-}

let usdN = base58''

@Callable(i)
func stake() = {
  if (i.payments[0].assetId != usdN)
    then
      throw("unexpected asset")
    else {
      let currentLiquidity = this.getInteger(i.originCaller.toString()).valueOrElse(0)
      [ IntegerEntry(i.originCaller.toString(), currentLiquidity + i.payments[0].amount) ]
    }
}

@Callable(i)
func cancelStake(amount: Int) = {
  if (this.getIntegerValue(i.caller.toString()) >= amount)
    then
      [ ScriptTransfer(i.caller, amount, usdN) ]
    else
      throw("too big amount")
}

